CREATE OR REPLACE FUNCTION obter_sexo_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_sexo_w	varchar(1);
ds_retorno_w	varchar(100);


BEGIN

-- ie_opcao_p 'C' Código  'D' Descrição
select	coalesce(max(ie_sexo),'X')
into STRICT	ie_sexo_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (ie_sexo_w <> 'X') then
	begin

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= ie_sexo_w;
	else
		select	substr(coalesce(ds_valor_dominio_cliente, obter_desc_expressao(cd_exp_valor_dominio, ds_valor_dominio)), 1, 255)
		into STRICT	ds_retorno_w
		from	valor_dominio
		where	cd_dominio	= 4
		and	vl_dominio	= ie_sexo_w;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
