CREATE OR REPLACE FUNCTION obter_sexo_prescricao ( nr_prescricao_p bigint) RETURNS char AS $body$
DECLARE


cd_recem_nato_w		pessoa_fisica.cd_pessoa_fisica%type;
ie_recem_nato_w		char(1);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
ie_retorno_w		char(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	max(cd_recem_nato),
			coalesce(max(ie_recem_nato),'N'),
			max(cd_pessoa_fisica)
	into STRICT	cd_recem_nato_w,
			ie_recem_nato_w,
			cd_pessoa_fisica_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	ie_retorno_w := '';

	if (cd_recem_nato_w IS NOT NULL AND cd_recem_nato_w::text <> '') then
		select	max(ie_sexo)
		into STRICT	ie_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_recem_nato_w;
	elsif (ie_recem_nato_w = 'N') then
		select	max(ie_sexo)
		into STRICT	ie_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
