CREATE OR REPLACE FUNCTION obter_se_aceita_fornec_lances ( nr_seq_fornec_p bigint, nr_seq_lic_item_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_aceitado_w			varchar(1) := 'N';
nr_seq_licitacao_w			bigint;
vl_vencedor_w			double precision;
pr_aceitavel_w			double precision;
vl_original_w			double precision;

BEGIN

select	nr_seq_licitacao
into STRICT	nr_seq_licitacao_w
from	reg_lic_fornec
where	nr_sequencia		= nr_seq_fornec_p
and	coalesce(ie_situacao, 'A')		<> 'I';

select	coalesce(max(obter_valor_param_usuario(952,2,obter_perfil_ativo, nm_usuario_p, 0)), '10')
into STRICT	pr_aceitavel_w
;

select	lic_obter_valor_vencedor_item(nr_seq_licitacao_w,nr_seq_lic_item_p)
into STRICT	vl_vencedor_w
;

select	coalesce(max(vl_original),0)
into STRICT	vl_original_w
from	reg_lic_item_fornec
where	nr_seq_licitacao		= nr_seq_licitacao_w
and	nr_seq_fornec		= nr_seq_fornec_p
and	nr_seq_lic_item		= nr_seq_lic_item_p
and	vl_original			> 0
and	coalesce(ie_qualificado, 'S')	= 'S'
and	coalesce(ie_qualif_lance_fornec,'S')	= 'S';

if	(vl_original_w <= (dividir((vl_vencedor_w * pr_aceitavel_w),100) + vl_vencedor_w)) then
	ie_aceitado_w := 'S';
end if;

return	ie_aceitado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
