CREATE OR REPLACE FUNCTION obter_se_acesso_bloqueado (ds_login_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno varchar(1) := '';

BEGIN
	select	CASE WHEN coalesce(max(ie_situacao),'A')='A' THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno
	from	controle_acesso_web
	where	ds_login = ds_login_p;
	return 	ds_retorno;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
