SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_acesso_paciente_lib (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_restrict_w	bigint;
qt_restrict_exist_w	bigint;
ie_restrict_w	varchar(1);


BEGIN

ie_restrict_w	:= 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_restrict_w
	from	PRIV_LOCALIZACAO_PACIENTE
	where	CD_PESSOA_FISICA	= cd_pessoa_fisica_p;

	if (qt_restrict_w > 0)	then
		select	count(*)
		into STRICT	qt_restrict_exist_w
		from	PRIV_LOCALIZACAO_PACIENTE
		where	CD_PESSOA_FISICA = cd_pessoa_fisica_p
		and	clock_timestamp() between DT_INICIO_BLOQUEIO and coalesce(DT_FIM_BLOQUEIO, clock_timestamp());

		if (qt_restrict_exist_w > 0)	then
			ie_restrict_w := 'N';
		else
			ie_restrict_w := 'S';
		end if;

	else
		ie_restrict_w :='S';
	end if;
end if;

return	ie_restrict_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
