CREATE OR REPLACE FUNCTION obter_se_achado_visto ( dt_achado_critico_p timestamp) RETURNS varchar AS $body$
DECLARE



ie_visualizado_w varchar(1);
nm_usuario_w  varchar(20);


BEGIN

nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

select CASE WHEN coalesce(max(1),0)=1 THEN 'S'  ELSE 'N' END
into STRICT ie_visualizado_w
from  pep_acesso_item
where nm_usuario_nrec = nm_usuario_w
and   nr_seq_item = 3
and dt_atualizacao_nrec > dt_achado_critico_p;

return ie_visualizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
