CREATE OR REPLACE FUNCTION obter_se_acm_sn_apraz (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprazado_w	varchar(1) := 'N';
ie_acm_sn_w	bigint;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select	coalesce(count(*),0)
	into STRICT	ie_acm_sn_w
	from 	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_p
	and	((ie_acm = 'S') or (ie_se_necessario = 'S'));

	if (ie_acm_sn_w > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_aprazado_w
		from	prescr_proc_hor
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_procedimento = nr_seq_prescr_p
		and	ie_aprazado = 'S'
		and	coalesce(ie_horario_especial,'N') = 'N'
		and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
	else
		ie_aprazado_w := 'S';
	end if;

end if;

return ie_aprazado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
