CREATE OR REPLACE FUNCTION obter_se_acomodacao_desej_disp ( cd_tipo_acomodacao_p bigint, dt_agendamento_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_regra_w		bigint;
ie_dia_da_semana_w	varchar(1);
ds_retorno_w		varchar(1):='S';
qt_reserva_w		bigint;
qt_vagas_w		bigint;


BEGIN 
 
IF (cd_tipo_acomodacao_p IS NOT NULL AND cd_tipo_acomodacao_p::text <> '') THEN 
	ie_dia_da_semana_w := Obter_Cod_Dia_Semana(dt_agendamento_p);
	SELECT	COUNT(*) 
	INTO STRICT	qt_regra_w 
	FROM	regra_reserva_vaga_acomod 
	WHERE	cd_tipo_acomodacao 	= cd_tipo_acomodacao_p 
	AND	ie_dia_semana		= ie_dia_da_semana_w 
	AND 	((cd_estabelecimento = cd_estabelecimento_p) OR (coalesce(cd_estabelecimento::text, '') = ''));
 
	IF (qt_regra_w > 0) THEN 
		SELECT	coalesce(MAX(qt_vagas),0) 
		INTO STRICT	qt_vagas_w 
		FROM 	regra_reserva_vaga_acomod 
		WHERE	cd_tipo_acomodacao 	= cd_tipo_acomodacao_p 
		AND	ie_dia_semana		= ie_dia_da_semana_w 
		AND 	((cd_estabelecimento = cd_estabelecimento_p) OR (coalesce(cd_estabelecimento::text, '') = ''));
 
		 
		SELECT	COUNT(*) 
		into STRICT	qt_reserva_w 
		FROM gestao_vaga a
LEFT OUTER JOIN atendimento_paciente b ON (a.nr_atendimento = b.nr_atendimento)
WHERE a.cd_tipo_acomod_desej 	= cd_tipo_acomodacao_p and a.dt_solicitacao between dt_agendamento_p - 60 and dt_agendamento_p AND fim_dia(dt_agendamento_p-1) BETWEEN coalesce(b.dt_entrada,a.dt_prevista) AND coalesce(b.dt_entrada,a.dt_prevista)+coalesce(a.qt_dia,1);
 
		IF (qt_reserva_w >= qt_vagas_w) THEN 
			ds_retorno_w := 'N';
		END IF;
	END IF;
END IF;
 
RETURN	ds_retorno_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
