CREATE OR REPLACE FUNCTION obter_se_acomod_lib_estab ( cd_convenio_p bigint, cd_categoria_p text, cd_tipo_acomod_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_aux_w	bigint;
qt_aux2_w	bigint;
ie_retorno_w	varchar(1):='S';


BEGIN

select	count(*)
into STRICT	qt_aux_w
from	cat_conv_tipo_acomodacao
where	cd_convenio = cd_convenio_p
and	cd_categoria = cd_categoria_p
and 	cd_estabelecimento = cd_estabelecimento_p;

if (qt_aux_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_aux_w
	from	cat_conv_tipo_acomodacao
	where	cd_convenio = cd_convenio_p
	and	cd_categoria = cd_categoria_p
	and	cd_tipo_acomodacao = cd_tipo_acomod_p
	and 	cd_estabelecimento = cd_estabelecimento_p;

	select	count(*)
	into STRICT	qt_aux2_w
	from	categoria_convenio
	where	cd_convenio		= cd_convenio_p
	and	cd_categoria		= cd_categoria_p
	and	cd_tipo_acomodacao	= cd_tipo_acomod_p;

	if (qt_aux_w = 0) and (qt_aux2_w = 0) then
		ie_retorno_w:= 'N';
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
