CREATE OR REPLACE FUNCTION obter_se_acomod_quimio_livre (nr_seq_local_p bigint) RETURNS varchar AS $body$
DECLARE


ie_acomod_livre_w	varchar(1);


BEGIN

select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_acomod_livre_w
from	paciente_atendimento
where 	coalesce(dt_fim_adm::text, '') = ''
and 	trunc(coalesce(dt_real,dt_prevista)) = trunc(clock_timestamp())
and	nr_seq_local	= nr_seq_local_p;


return	ie_acomod_livre_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
