CREATE OR REPLACE FUNCTION obter_se_adep_tipo_obs (nr_seq_adep_obs_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
cont_w		bigint;


BEGIN

ds_retorno_w	:= 'S';

select	count(*)
into STRICT	cont_w
from	regra_adep_tipo_obs
where	nr_seq_regra	= nr_seq_adep_obs_p;

if (coalesce(cont_w, 0) > 0) then

	select	count(*)
	into STRICT	cont_w
	from	regra_adep_tipo_obs
	where	nr_seq_regra	= nr_seq_adep_obs_p
	and	cd_perfil	= cd_perfil_p;

	if (coalesce(cont_w, 0) = 0) then

		ds_retorno_w	:= 'N';

	end if;

end if;

return ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
