CREATE OR REPLACE FUNCTION obter_se_adiantamento (cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint, dt_limite_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1)	:= 'N';
cont_w		bigint	:= 0;
cd_pessoa_w	varchar(14)	:= null;


BEGIN 
ie_retorno_w	:= 'N';
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	cd_pessoa_w	:= cd_pessoa_fisica_p;
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then 
	cd_pessoa_w	:= cd_cgc_p;	
end if;
 
if (cd_pessoa_w IS NOT NULL AND cd_pessoa_w::text <> '') then 
	select	count(*) 
	into STRICT	cont_w 
	from	adiantamento 
	where	obter_saldo_adiantamento(nr_adiantamento,clock_timestamp()) > 0 
	and (trunc(dt_adiantamento,'dd') <= trunc(dt_limite_p,'dd') or coalesce(dt_limite_p::text, '') = '') 
	and	cd_estabelecimento	= cd_estabelecimento_p 
	and	coalesce(cd_pessoa_fisica,cd_cgc)	= cd_pessoa_w;
end if;
 
if (cont_w > 0) then 
	ie_retorno_w	:= 'S';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
