CREATE OR REPLACE FUNCTION obter_se_adiant_baixado_data ( nr_adiantamento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


/*
'B'	- Baixado
'A'	- Em aberto
*/
dt_baixa_w	timestamp;
ds_retorno_w	varchar(10);


BEGIN

select	max(dt_baixa)
into STRICT	dt_baixa_w
from	adiantamento_pago
where	nr_adiantamento	= nr_adiantamento_p;

if (coalesce(dt_baixa_w::text, '') = '') or (trunc(dt_baixa_w,'dd')	> trunc(dt_referencia_p,'dd')) then
	ds_retorno_w	:= 'A';
else
	ds_retorno_w	:= 'B';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
