CREATE OR REPLACE FUNCTION obter_se_adicionar_processo ( nr_atendimento_p bigint, dt_horario_p timestamp, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_adicionar_w	varchar(1) := 'N';


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then 
 
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	ie_adicionar_w 
	from	adep_pend_v 
	where	nr_atendimento		= nr_atendimento_p 
	and	dt_horario			= dt_horario_p 
	and	ie_tipo_item		in ('M', 'MAP') 
	and	coalesce(cd_local_estoque,0)	= coalesce(cd_local_estoque_p,0) 
	and	coalesce(nr_seq_processo::text, '') = '';	
 
end if;
 
return ie_adicionar_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
