CREATE OR REPLACE FUNCTION obter_se_aditivo_contr (cd_estabelecimento_p bigint, nr_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

 
			 
ie_retorno_w			varchar(1);
qt_aditivo_w			integer;			
ie_aditivo_contrato_nf_w 	varchar(1);

BEGIN
 
 
select 	distinct 
	ie_aditivo_contrato_nf 
into STRICT	ie_aditivo_contrato_nf_w	 
from 	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_aditivo_contrato_nf_w = 'N') then 
	select 	count(*) 
	into STRICT	qt_aditivo_w 
	from 	contrato 
	where	nr_sequencia = nr_contrato_p 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	ie_classificacao in ('AD','OR');
 
	if (qt_aditivo_w > 0) then 
		ie_retorno_w := 'S';
	end if;	
 
	if (qt_aditivo_w = 0)then 
		ie_retorno_w := 'N';
	end if;	
end if;
 
if (ie_aditivo_contrato_nf_w = 'S') then 
	ie_retorno_w := 'T';
end if;
	 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
