CREATE OR REPLACE FUNCTION obter_se_adm_hor_processo (nr_atendimento_p bigint, cd_material_p bigint, cd_intervalo_p text, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_adm_w	varchar(1);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN  'S'  ELSE 'N' END
	into STRICT	ie_adm_w
	from	prescr_mat_hor c,
		prescr_material b,
		prescr_mat_alteracao a
	where	c.nr_prescricao		= b.nr_prescricao
	and	c.nr_seq_material		= b.nr_sequencia
	and	b.nr_prescricao 		= a.nr_prescricao
	and	b.cd_material			= a.cd_item
	and	(c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')
	and	coalesce(c.ie_adep,'S')	= 'S'
	and	b.ie_agrupador		= 1
	and	b.cd_material			= cd_material_p
	and	coalesce(b.cd_intervalo,'XPTO')	= coalesce(cd_intervalo_p,'XPTO')
	and	a.nr_atendimento		= nr_atendimento_p
	and	a.dt_horario			= dt_horario_p
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S';


end if;

return ie_adm_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
