CREATE OR REPLACE FUNCTION obter_se_adm_item_pda ( ie_tipo_item_p text, cd_perfil_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_adm_w	varchar(1);


BEGIN

if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then

select	max(coalesce(ie_permite,'N'))
into STRICT	ie_adm_w
from	adep_adm_item_pda
where	ie_tipo_item 					= ie_tipo_item_p
and		coalesce(cd_perfil, cd_perfil_p) 	= cd_perfil_p
and		coalesce(cd_setor_atendimento, cd_setor_atendimento_p) 	= cd_setor_atendimento_p;

end if;

return coalesce(ie_adm_w,'N');

end;
$body$
LANGUAGE PLPGSQL
