CREATE OR REPLACE FUNCTION obter_se_ageint_mult ( nr_seq_agenda_cons_p bigint, nr_seq_agenda_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_w		varchar(255) := 'N';
ie_liberado_w   varchar(2) := 'N';
cd_pais_w       smallint;


BEGIN

select  obter_nr_seq_locale(wheb_usuario_pck.get_nm_usuario)
into STRICT    cd_pais_w
;

select  coalesce(max('S'),'N')
into STRICT    ie_liberado_w
from    tab_pais
where   nr_seq_tab_ref = 1033075
and     cd_pais = cd_pais_w;

if (ie_liberado_w = 'S')then
    if (nr_seq_agenda_cons_p IS NOT NULL AND nr_seq_agenda_cons_p::text <> '') then
        select 	coalesce(max('S'),'N')
        into STRICT	ie_possui_w
        from 	agenda_integrada_item
        where	nr_seq_agenda_cons = nr_seq_agenda_cons_p
        and	ie_origem_agendamento in ('CP','MS');
    elsif (nr_seq_agenda_exame_p IS NOT NULL AND nr_seq_agenda_exame_p::text <> '') then
        select 	coalesce(max('S'),'N')
        into STRICT	ie_possui_w
        from 	agenda_integrada_item
        where	nr_seq_agenda_exame = nr_seq_agenda_exame_p
        and	ie_origem_agendamento in ('CP','MS');
    end if;
else
  ie_possui_w := 'N';
end if;

return ie_possui_w;

end;
$body$
