CREATE OR REPLACE FUNCTION obter_se_ageint_transf ( nr_seq_agenda_int_p bigint, cd_pessoa_fisica_p text, dt_agenda_p timestamp, nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';


BEGIN


if (coalesce(nr_seq_agenda_int_p::text, '') = '')then
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	agenda_integrada a,
			agenda_integrada_item b,
			agenda_paciente c
	where	a.nr_sequencia 			= b.nr_seq_agenda_int
	and		b.nr_seq_agenda_exame	= c.nr_sequencia
	and		a.cd_pessoa_fisica		= cd_pessoa_fisica_p
	and		b.ie_tipo_agendamento	= 'E'
	and		(b.dt_transferencia IS NOT NULL AND b.dt_transferencia::text <> '')
	and		c.hr_inicio				> dt_agenda_p
	and		c.nr_seq_proc_interno	= nr_seq_proc_interno_p;
else
	ds_retorno_w	:= 'N';

end if;

return	ds_retorno_w;

end;
$body$
