CREATE OR REPLACE FUNCTION obter_se_agendamento_pendente (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ie_agendamento_pendente	varchar(1);	
nr_agendamento_pendente	bigint;		

BEGIN 
 
select	count(*) 
into STRICT	nr_agendamento_pendente 
from	agenda_consulta c, 
	agenda a 
where	c.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	a.cd_tipo_agenda = 3 
and	a.cd_agenda    = c.cd_agenda;
	 
if (nr_agendamento_pendente > 0) then 
	ie_agendamento_pendente := 'S';
else 
	ie_agendamento_pendente := 'N';
end if;
 
return	ie_agendamento_pendente;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
