CREATE OR REPLACE FUNCTION obter_se_agenda_a_seguir_html ( nr_seq_agenda_p bigint, hr_inicio_p timestamp, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_as_w		varchar(1);
cd_medico_w	varchar(10);


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT		ie_as_w
	from		agenda_paciente x
	where		x.hr_inicio between 	hr_inicio_p - (10/1440)  and hr_inicio_p
	and		(x.cd_medico IS NOT NULL AND x.cd_medico::text <> '')
	and		x.cd_medico				= 	cd_medico_p
	and		x.nr_sequencia			<> nr_seq_agenda_p
	and		x.ie_status_agenda	<> 'L';
end if;

return	ie_as_w;

end;
$body$
