CREATE OR REPLACE FUNCTION obter_se_agenda_dia (dt_agenda_p timestamp, cd_pessoa_fisica_p text, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_dia_w	varchar(1) := 'N';


BEGIN 
if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	ie_dia_w 
	from	agenda b, 
		agenda_paciente a 
	where	b.cd_agenda = a.cd_agenda 
	and	b.cd_tipo_agenda = 2 
	and	a.dt_agenda between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400 
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.nr_sequencia <> nr_seq_agenda_p 
	and	coalesce(a.nr_atendimento::text, '') = '' 
	and	a.ie_status_agenda not in ('F','I','C','B','E');
end if;
 
return ie_dia_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
