CREATE OR REPLACE FUNCTION obter_se_agenda_encaixe (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE



ie_encaixe_w		varchar(01) := 'N';
nr_seq_classif_w	bigint := 0;


BEGIN

select	coalesce(max(NR_SEQ_CLASSIF_AGENDA),0)
into STRICT	nr_seq_classif_w
from	agenda_paciente_classif b, agenda_paciente a
where	a.nr_seq_classif_agenda = b.nr_sequencia
and	b.ie_tipo_classif	= 'E'
and	a.nr_sequencia		= nr_seq_agenda_p;

if (nr_seq_classif_w > 0) then
	ie_encaixe_w	:= 'S';
end if;

return	ie_encaixe_w;

end;
