CREATE OR REPLACE FUNCTION obter_se_agenda_escala ( cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


qt_escala_w	integer;
ds_retorno_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_escala_w
from	agenda_escala
where	cd_agenda = cd_agenda_p;

if (qt_escala_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
