CREATE OR REPLACE FUNCTION obter_se_agenda_espec_medico ( cd_pessoa_fisica_p text, cd_especialidade_agenda_p bigint, ie_atualizar_esp_p text) RETURNS bigint AS $body$
DECLARE

 
cd_especialidade_w varchar(5);
