BEGIN 
 
ds_retorno_w := 0;
 
if (ie_atualizar_esp_p = 'E') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	cd_especialidade_w := substr(obter_especialidade_medico(cd_pessoa_fisica_p,'C'),1,5);
