SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_agenda_liberada (ie_periodo_p text, cd_versao_p text, ds_hotfix_p text) RETURNS varchar AS $body$
DECLARE

				
ie_status_w		varchar(1);
qt_bloqueio_w	 bigint;
qt_periodo_w	bigint;


BEGIN

ie_status_w:= 'S';

select	count(*) qt
into STRICT	qt_periodo_w
from	agendamento_build_block a
where	a.ie_periodo = ie_periodo_p;

if (qt_periodo_w > 0) then

	select	count(*) qt
	into STRICT	qt_bloqueio_w
	from	agendamento_build_block a
	where	a.cd_versao = cd_versao_p
	and	coalesce(a.ds_hotfix,'0') = coalesce(ds_hotfix_p,'0')
	and	a.ie_periodo = ie_periodo_p
	order by 1;
	
	if (qt_bloqueio_w = 0) then
		ie_status_w:= 'N';
	end if;

end if;


return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
