CREATE OR REPLACE FUNCTION obter_se_agenda_med_liberada ( cd_agenda_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


retorno_w	char(1);
total_w		integer;
ie_libera_w	smallint;


BEGIN

retorno_w	:= 'N';

select 	count(*)
into STRICT	total_w
from   	med_permissao_agenda d
where  	d.cd_agenda 	= cd_agenda_p;

if (total_w 	= 	0) 	then
	retorno_w := 'S';
else
	select 	count(*)
	into STRICT	ie_libera_w
	from   	med_permissao_agenda d
	where  	d.cd_agenda 	= cd_agenda_p
	and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_libera_w 	<> 	0) 	then
		retorno_w := 'S';
	end if;

end if;

return	retorno_w;

end;
