CREATE OR REPLACE FUNCTION obter_se_agend_adic_repetido (nr_seq_agenda_p text, cd_procedimento_p text, ie_origem_proced_p text, nr_seq_proc_interno_p text, nr_seq_exame_p text, cd_tipo_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';

c01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno,
		nr_seq_exame
	from	agenda_consulta_proc
	where	nr_seq_agenda = nr_seq_agenda_p;

c02 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno
	from 	agenda_paciente_proc
	where	nr_sequencia = nr_seq_agenda_p;
BEGIN

--agenda de consultas
if (cd_tipo_agenda_p  = 3) then

	for r_c01 in c01 loop

		if((r_c01.cd_procedimento = cd_procedimento_p
			and r_c01.ie_origem_proced = ie_origem_proced_p)
		or (r_c01.nr_seq_proc_interno = nr_seq_proc_interno_p)
		or (r_c01.nr_seq_exame = nr_seq_exame_p)) then

			ie_retorno_w := 'S';
		end if;
	end loop;
 --agenda  servi√ßo
elsif (cd_tipo_agenda_p = 5) then

	for r_c01 in c01 loop

		if((r_c01.cd_procedimento = cd_procedimento_p
			and r_c01.ie_origem_proced = ie_origem_proced_p)
		or (r_c01.nr_seq_proc_interno = nr_seq_proc_interno_p)
		or (r_c01.nr_seq_exame = nr_seq_exame_p)) then

			ie_retorno_w := 'S';
		end if;
	end loop;

--agenda de exame
elsif (cd_tipo_agenda_p = 2)then

	for r_c02 in c02 loop

		if((r_c02.cd_procedimento = cd_procedimento_p
			and r_c02.ie_origem_proced = ie_origem_proced_p)
		or (r_c02.nr_seq_proc_interno = nr_seq_proc_interno_p)) then

			ie_retorno_w := 'S';
		end if;
	end loop;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
