CREATE OR REPLACE FUNCTION obter_se_agrupar_processo_area ( nr_processo_origem_p bigint, nr_processo_destino_p bigint, nr_seq_area_prep_p bigint) RETURNS varchar AS $body$
DECLARE


ie_agrupar_w		varchar(1);
ie_existe_origem_w	varchar(1);
ie_existe_destino_w	varchar(1);
ie_status_origem_w	varchar(1);
ie_status_destino_w	varchar(1);


BEGIN
if (nr_processo_origem_p IS NOT NULL AND nr_processo_origem_p::text <> '') and (nr_processo_destino_p IS NOT NULL AND nr_processo_destino_p::text <> '') and (nr_seq_area_prep_p IS NOT NULL AND nr_seq_area_prep_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_origem_w
	from	adep_processo_area
	where	nr_seq_processo = nr_processo_origem_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_destino_w
	from	adep_processo_area
	where	nr_seq_processo = nr_processo_origem_p;

	if (ie_existe_origem_w = 'S') and (ie_existe_destino_w = 'S') then

		ie_status_origem_w	:= obter_status_processo_area(nr_processo_origem_p, nr_seq_area_prep_p);
		ie_status_destino_w	:= obter_status_processo_area(nr_processo_destino_p, nr_seq_area_prep_p);

		if (ie_status_origem_w = ie_status_destino_w) then
			ie_agrupar_w	:= 'S';
		else
			ie_agrupar_w	:= 'N';
		end if;

	else
		ie_agrupar_w	:= 'S';
	end if;

end if;

return ie_agrupar_w;

end;
$body$
LANGUAGE PLPGSQL
