SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_agrup_audit_ext ( nr_seq_regra_audit_ext_p bigint, nr_sequencia_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1):= 'S';
cd_material_w		integer;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ie_agrupar_w		varchar(1);
cd_grupo_material_w     smallint;
cd_subgrupo_material_w  smallint;
cd_classe_material_w    integer;

cd_area_procedimento_w  bigint;
cd_especialidade_w      bigint;
cd_grupo_proc_w         bigint;

C01 CURSOR FOR
	SELECT	coalesce(ie_agrupar,'S')
	from	regra_audit_ext_crit_mat
	where	nr_seq_regra_audit = nr_seq_regra_audit_ext_p
	and 	ie_situacao = 'A'
	and 	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
	and 	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and 	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
	and 	coalesce(cd_material, cd_material_w) = cd_material_w
	order by coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0);

C02 CURSOR FOR
	SELECT	coalesce(ie_agrupar,'S')
	from	regra_audit_ext_crit_proc
	where	nr_seq_regra_audit = nr_seq_regra_audit_ext_p
	and 	ie_situacao = 'A'
	and 	coalesce(cd_area_procedimento, cd_area_procedimento_w) = cd_area_procedimento_w
	and 	coalesce(cd_especialidade, cd_especialidade_w) = cd_especialidade_w
	and 	coalesce(cd_grupo_proc, cd_grupo_proc_w) = cd_grupo_proc_w
	and 	coalesce(cd_procedimento, cd_procedimento_w) = cd_procedimento_w
	order by coalesce(cd_procedimento,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);


BEGIN

if (ie_tipo_item_p = 'P') then

	select 	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from 	procedimento_paciente
	where 	nr_sequencia = nr_sequencia_p;

	select 	cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc
	into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from 	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_w
	and 	ie_origem_proced = ie_origem_proced_w;

	open C02;
	loop
	fetch C02 into
		ie_agrupar_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ds_retorno_w:= ie_agrupar_w;
		end;
	end loop;
	close C02;

elsif (ie_tipo_item_p = 'M') then

	select 	max(cd_material)
	into STRICT	cd_material_w
	from 	material_atend_paciente
	where 	nr_sequencia = nr_sequencia_p;

	select 	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from 	estrutura_material_v
	where 	cd_material = cd_material_w;

	open C01;
	loop
	fetch C01 into
		ie_agrupar_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w:= ie_agrupar_w;
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
