CREATE OR REPLACE FUNCTION obter_se_agrup_lib_perfil ( nr_seq_agrup_p bigint) RETURNS varchar AS $body$
DECLARE


qt_cadastro_w		integer;
ie_permite_w		varchar(1) := 'S';
cd_perfil_w		integer;
ie_considerar_regra_w	varchar(1);

C01 CURSOR FOR
SELECT	ie_permite,
	cd_perfil
from	agrupamento_setor_lib
where	nr_seq_agrupamento = nr_seq_agrup_p
and	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo
AND	coalesce(ie_ocupacao,'N') = 'S'
order by cd_perfil desc;


BEGIN
ie_considerar_regra_w := Obter_param_Usuario(44, 258, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_considerar_regra_w);

if (ie_considerar_regra_w = 'S') then

	begin
	select	1
	into STRICT	qt_cadastro_w
	from	agrupamento_setor_lib
	where	nr_seq_agrupamento = nr_seq_agrup_p  LIMIT 1;

	exception
	when no_data_found then
		qt_cadastro_w := 0;
	end;

	open C01;
	loop
	fetch C01 into
		ie_permite_w,
		cd_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
