CREATE OR REPLACE FUNCTION obter_se_agrup_lib (cd_perfil_p bigint, nr_seq_agrup_p bigint, ie_restricao_p text) RETURNS varchar AS $body$
DECLARE


qt_cadastro_w		integer;
ie_permite_w		varchar(1) := 'N';
cd_perfil_w		integer;

C01 CURSOR FOR
SELECT	ie_permite,
	cd_perfil
from	agrupamento_setor_lib
where	ie_titulo_receber = 'S'
and	nr_seq_agrupamento = nr_seq_agrup_p
and	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
and	ie_restricao_p = 'TR'

union all

select	ie_permite,
	cd_perfil
from	agrupamento_setor_lib
where	ie_eup = 'S'
and	nr_seq_agrupamento = nr_seq_agrup_p
and	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
and	ie_restricao_p = 'EUP'
order by cd_perfil desc;


BEGIN

select	count(*)
into STRICT	qt_cadastro_w
from	agrupamento_setor_lib
where	nr_seq_agrupamento = nr_seq_agrup_p;

if (qt_cadastro_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_permite_w,
		cd_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
else
	ie_permite_w := 'S';
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
