CREATE OR REPLACE FUNCTION obter_se_ajustado_auditoria ( nr_seq_item_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_p
	1	=	Procedimentos
	2	=	Materiais
*/
ie_ajuste_w	varchar(1):=	'N';


BEGIN

if (ie_tipo_p = '1') then
	begin
	select 	max(CASE WHEN coalesce(qt_ajuste::text, '') = '' THEN 'N'  ELSE 'S' END )
	into STRICT	ie_ajuste_w
	from 	auditoria_propaci
	where 	nr_seq_propaci = nr_seq_item_p;
	exception
		when others then
		 	ie_ajuste_w:= 'N';
	end;
elsif (ie_tipo_p = '2') then
	begin
	select 	max(CASE WHEN coalesce(qt_ajuste::text, '') = '' THEN 'N'  ELSE 'S' END )
	into STRICT	ie_ajuste_w
	from 	auditoria_matpaci
	where 	nr_seq_matpaci = nr_seq_item_p;
	exception
		when others then
		 	ie_ajuste_w:= 'N';
	end;
end if;

return ie_ajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
