CREATE OR REPLACE FUNCTION obter_se_alerta_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_alerta_w	varchar(1) := 'N';


BEGIN 
ie_alerta_w	:= 'N';
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_alerta_w 
	from	atendimento_alerta 
	where	nr_atendimento = nr_atendimento_p 
	and	ie_situacao ='A' 
	and	coalesce(dt_fim_alerta,clock_timestamp()) >= clock_timestamp() 
	and	obter_se_tipo_alerta_lib(nr_seq_tipo_alerta,wheb_usuario_pck.get_nm_usuario)	= 'S';
 
end if;
 
return ie_alerta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
