CREATE OR REPLACE FUNCTION obter_se_alerta_item_duplic ( nr_atendimento_p bigint, cd_material_p bigint, dt_horario_p timestamp, qt_min_p bigint, nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_alerta_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N') ie_alerta
into STRICT	ie_alerta_w
from	prescr_medica a,
	prescr_material b,
	prescr_mat_hor c
where	a.nr_prescricao  = b.nr_prescricao
and	b.nr_prescricao  = c.nr_prescricao
and	b.nr_sequencia   = c.nr_seq_material
and	a.nr_atendimento = nr_atendimento_p
and	b.cd_material    = cd_material_p
and	((b.nr_prescricao <> nr_prescricao_p) or (b.nr_sequencia <> nr_seq_material_p))
and	a.dt_validade_prescr between dt_horario_p - 2 and dt_horario_p + 2
and	coalesce(c.dt_fim_horario::text, '') = ''
and	coalesce(c.dt_suspensao::text, '') = ''
and	c.dt_horario between(dt_horario_p-qt_min_p/1440) and (dt_horario_p+qt_min_p/1440);

return	ie_alerta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
