CREATE OR REPLACE FUNCTION obter_se_alerta_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

begin
select	'S'
into STRICT	ds_retorno_w
from	alerta_paciente
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_inativacao::text, '') = ''
and	((coalesce(dt_fim_alerta::text, '') = '') or (dt_fim_alerta >= trunc(clock_timestamp(),'dd')))  LIMIT 1;
exception
when others then
	ds_retorno_w := 'N';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
