CREATE OR REPLACE FUNCTION obter_se_alerta_pf_usuario (dt_inicial_p timestamp, dt_final_p timestamp, cd_paciente_p text, cd_pf_destino_p text, ie_radioterapia_p text, ie_acao_p text) RETURNS varchar AS $body$
DECLARE


qt_regra_w	bigint			;
ie_retorno_w	varchar(1) := 'N';

/* ie_acao_p
	AC 	- Agendamento de Consulta
	ASC	- Alterar status Agenda de Consultas

	IT	- Incluir tratamento
	AT	- Agendar tratamento
	ASR	- Alterar status da radioterapia
	AP	- Aplicar dose
*/
BEGIN

if (ie_radioterapia_p = 'S') then
	select	count(*)
	into STRICT	qt_regra_w
	from	rxt_regra_evento
	where	ie_acao	= ie_acao_p
	and	coalesce(ie_situacao,'A') = 'A';
else
	select	count(*)
	into STRICT	qt_regra_w
	from	agecons_regra_evento
	where	ie_acao	= ie_acao_p
	and	coalesce(ie_situacao,'A') = 'A';
end if;

if (qt_regra_w > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	ev_evento a,
		ev_evento_paciente b,
		ev_evento_regra_dest c,
		ev_evento_pac_destino d
	where   a.nr_sequencia = b.nr_seq_evento
	and     c.nr_seq_evento = a.nr_sequencia
	and	d.nr_seq_ev_pac = B.nr_sequencia
	and     b.dt_evento    between dt_inicial_p and dt_final_p
	and     coalesce(b.ie_situacao,'A') = 'A'
	and     c.ie_forma_ev  = '2'
	and 	d.cd_pessoa_fisica = cd_pf_destino_p
	and	b.cd_pessoa_fisica = cd_paciente_p
	and	d.ie_status = 'G';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
