CREATE OR REPLACE FUNCTION obter_se_alerta_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_alerta_w	varchar(1) := 'N';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_alerta_w
	from	pf_alerta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_ciencia::text, '') = ''
	and	clock_timestamp() between coalesce(DT_INICIO,clock_timestamp()) and coalesce(DT_FIM,clock_timestamp());

end if;

return ie_alerta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
