SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_alerta_prof_res ( cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(100) := 'N';
ie_entregue_w				varchar(1);
ds_caminho_anexo_w		varchar(100);
ds_codigo_externo_w		varchar(50) := 'PDF_PROFISSIONAL';
nr_sequencia_w			bigint;


BEGIN

select max(a.nr_sequencia)
into STRICT nr_sequencia_w
from pessoa_documentacao a
where (a.ds_arquivo IS NOT NULL AND a.ds_arquivo::text <> '')
and a.cd_pessoa_fisica = cd_medico_p
and exists (
SELECT b.cd_interno from conversao_meio_externo b
		   		where  	b.nm_tabela = 'PESSOA_DOCUMENTACAO'
				and		b.nm_atributo = 'IE_DOC_EXTERNO'
				and		b.cd_interno  = a.nr_seq_documento
				and		b.cd_externo = 'ACTPF'
				and    	b.ie_sistema_externo = 'TERUNIMED16');
				
if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	select ie_entregue
	into STRICT ie_entregue_w
	from pessoa_documentacao
	where nr_sequencia = nr_sequencia_w;
	
	if (ie_entregue_w = 'N') then

		select obter_conversao_externa_int(NULL, 'PDF_PROFISSIONAL', 'PDF_PROFISSIONAL', 'PDF_PROFISSIONAL' , 'TERUNIMED16')
		into STRICT ds_caminho_anexo_w
		;
		
		if (ds_caminho_anexo_w <> ds_codigo_externo_w) then
		
			ds_retorno_w := ds_caminho_anexo_w;
			
		
		end if;
	
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
