CREATE OR REPLACE FUNCTION obter_se_alta_atrasada (dt_alta_p timestamp, dt_saida_real_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_retorno_w	bigint;
qt_minuto_w	bigint;
ie_atraso_w	varchar(1);

	/*
		Verifica se o paciente está com a alta atrasada conforme
		parâmetro [72] - Quantidade de minutos na pasta alta para atualizar a cor de alerta, caso não tenha saída real
		da função Ocupação Hospitalar.
	*/
BEGIN

if (dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') and (coalesce(dt_saida_real_p::text, '') = '') then
	begin
	select	max(obter_valor_param_usuario(44, 72, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p))
	into STRICT	qt_minuto_w
	;

	select	trunc(((clock_timestamp() - dt_alta_p) * 1440))
	into STRICT 	qt_retorno_w
	;

	if (qt_retorno_w > coalesce(qt_minuto_w,0)) then
		ie_atraso_w	:= 'S';
	else
		ie_atraso_w	:= 'N';
	end if;
	end;
else
	ie_atraso_w := 'N';

end if;

return	ie_atraso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
