CREATE OR REPLACE FUNCTION obter_se_alta_lib (cd_motivo_alta_p bigint, cd_setor_atendimento_p bigint, ie_tipo_atendimento_p text, cd_perfil_p bigint, ie_tipo text) RETURNS varchar AS $body$
DECLARE


ie_retorno_W			varchar(2)	:= 'N';
qt_existe_w				bigint;
cd_motivo_alta_w		bigint;

/* ie_tipo = M - Função Movimentação de Pacientes
    ie_itpo = E -  Entrada Única de Pacientes
*/
c01 CURSOR FOR
SELECT	a.cd_motivo_alta
from	motivo_alta a
where	a.cd_motivo_alta = cd_motivo_alta_p
and     ((A.ie_tipo_atend_excluir <> ie_tipo_atendimento_p) or (coalesce(A.ie_tipo_atend_excluir::text, '') = ''))
and     ((coalesce(A.cd_perfil::text, '') = '') or (A.cd_perfil = cd_perfil_p))
and     ((A.ie_tipo_atendimento = ie_tipo_atendimento_p) or (coalesce(A.ie_tipo_atendimento::text, '') = ''));

C02 CURSOR FOR
SELECT	a.cd_motivo_alta
from	motivo_alta a
where	a.cd_motivo_alta = cd_motivo_alta_p
and     ((A.ie_tipo_atend_excluir <> ie_tipo_atendimento_p) or (coalesce(A.ie_tipo_atend_excluir::text, '') = ''))
and     ((A.ie_tipo_atendimento = ie_tipo_atendimento_p) or (coalesce(A.ie_tipo_atendimento::text, '') = ''));


BEGIN

if (ie_tipo = 'M') then
	begin
	open C01;
	loop
	fetch C01 into
	cd_motivo_alta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_retorno_W	:= 'S';

		select	count(*)
		into STRICT 	qt_existe_w
		from	regra_motivo_alta_setor
		where	cd_motivo_alta = cd_motivo_alta_w;

		if (qt_existe_w > 0) then
			begin
			select	count(*)
			into STRICT 	qt_existe_w
			from	regra_motivo_alta_setor
			where	cd_motivo_alta = cd_motivo_alta_w
			and	cd_setor_atendimento = cd_setor_atendimento_p;

			if (qt_existe_w > 0) then
				ie_retorno_W	:= 'S';
			else
				ie_retorno_W	:= 'N';
			end if;
			end;
		end if;
		end;
	end loop;
	close C01;
	end;
elsif (ie_tipo = 'E') then
	begin
	open C02;
	loop
	fetch C02 into
		cd_motivo_alta_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ie_retorno_W	:= 'S';

		select	count(*)
		into STRICT 	qt_existe_w
		from	regra_motivo_alta_setor
		where	cd_motivo_alta = cd_motivo_alta_w;

		if (qt_existe_w > 0) then
			begin
			select	count(*)
			into STRICT 	qt_existe_w
			from	regra_motivo_alta_setor
			where	cd_motivo_alta = cd_motivo_alta_w
			and	cd_setor_atendimento = cd_setor_atendimento_p;

			if (qt_existe_w > 0) then
				ie_retorno_W	:= 'S';
			else
				ie_retorno_W	:= 'N';
			end if;
			end;
		end if;
		end;
	end loop;
	close C02;
	end;
end if;

return	ie_retorno_W;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
