CREATE OR REPLACE FUNCTION obter_se_alta_tesouraria2 ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_busca_historico_p text default 'S') RETURNS varchar AS $body$
DECLARE


cd_setor_w		bigint;
ds_retorno_w		varchar(255);
ie_exibe_status_w 	varchar(1);
nr_seq_unidade_w  	bigint;
nr_atendimento_unid_w	bigint;


BEGIN

ie_exibe_status_w := obter_param_usuario(44, 172, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_exibe_status_w);
cd_setor_w := 0;
if (ie_exibe_status_w = 'S') then
	if (coalesce(nr_atendimento_p::text, '') = '')
	and (ie_busca_historico_p = 'S')then
		select 	max(nr_seq_interno)
		into STRICT 	nr_seq_unidade_w
		from 	unidade_atendimento
		where 	cd_setor_atendimento = cd_setor_atendimento_p
		and 	cd_unidade_basica = cd_unidade_basica_p
		and 	cd_unidade_compl = cd_unidade_compl_p
		and	ie_status_unidade = 'A';

		If (nr_seq_unidade_w IS NOT NULL AND nr_seq_unidade_w::text <> '')
		and (nr_seq_unidade_w > 0) then
			select	max(nr_atendimento)
			into STRICT	nr_atendimento_unid_w
			from	unidade_atend_hist
			where	nr_sequencia = (SELECT	MAX(c.nr_sequencia)
						FROM    unidade_atend_hist c
						WHERE   c.nr_seq_unidade = nr_seq_unidade_w);

			if (nr_atendimento_unid_w IS NOT NULL AND nr_atendimento_unid_w::text <> '')
			and (nr_atendimento_unid_w > 0) then
				select	coalesce(max(1),0)
				into STRICT 	cd_setor_w
				from 	atendimento_paciente a
				where	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '')
				and	coalesce(a.dt_saida_real::text, '') = ''
				and	coalesce(a.dt_alta_tesouraria::text, '') = ''
				and	obter_se_tem_conta_particular(a.nr_atendimento) = 'S'
				and	(obter_conta_particular_aberta(a.nr_atendimento) IS NOT NULL AND (obter_conta_particular_aberta(a.nr_atendimento))::text <> '')
				and 	a.nr_atendimento = nr_atendimento_unid_w;
			end if;
		end if;
	else
		select 	max(nr_seq_interno)
		into STRICT 	nr_seq_unidade_w
		from 	unidade_atendimento
		where 	cd_setor_atendimento = cd_setor_atendimento_p
		and 	cd_unidade_basica = cd_unidade_basica_p
		and 	cd_unidade_compl = cd_unidade_compl_p
		and	ie_status_unidade = 'A';

		select	coalesce(max(1),0)
		into STRICT 	cd_setor_w
		from 	atendimento_paciente a
		where	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '')
		and	coalesce(a.dt_saida_real::text, '') = ''
		and	coalesce(a.dt_alta_tesouraria::text, '') = ''
		and	obter_se_tem_conta_particular(a.nr_atendimento) = 'S'
		and	(obter_conta_particular_aberta(a.nr_atendimento) IS NOT NULL AND (obter_conta_particular_aberta(a.nr_atendimento))::text <> '')
		and 	a.nr_atendimento = nr_atendimento_p;
	end if;
end if;

if (cd_setor_w > 0)
and (ie_exibe_status_w = 'S') then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
