CREATE OR REPLACE FUNCTION obter_se_alteracao_medicamento ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_houve_alteracao_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_houve_alteracao_w
from	prescr_material_alteracao
where	nr_prescricao 	= nr_prescricao_p
and	nr_seq_prescricao 	= nr_sequencia_p;

return	ie_houve_alteracao_w;

end	;
$body$
