CREATE OR REPLACE FUNCTION obter_se_alterado_status (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_ult_seq_w		bigint;
ie_result_w		varchar(1) := 'N';


BEGIN

select 	min(nr_sequencia)
into STRICT	nr_sequencia_w
from 	prescr_proc_etapa
where 	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescr_p
and	ie_etapa = 	(SELECT max(ie_etapa)
			from prescr_proc_etapa
			where nr_prescricao = nr_prescricao_p
			and nr_seq_prescricao = nr_seq_prescr_p);

select	max(nr_sequencia)
into STRICT 	nr_ult_seq_w
from	prescr_proc_etapa
where 	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescr_p;


if (nr_ult_seq_w > nr_sequencia_w) then
	ie_result_w := 'S';
end if;

return ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
