CREATE OR REPLACE FUNCTION obter_se_altera_adiant_pago (nr_adiantamento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_permite_alterar_w	varchar(255);


BEGIN 
 
select	CASE WHEN coalesce(sum(qt_registro),0)=0 THEN  'S'  ELSE 'N' END  ie_permite_alterar 
into STRICT	ie_permite_alterar_w 
from ( 
 
SELECT	1 qt_registro 
from	titulo_pagar_adiant a, 
			titulo_pagar b 
where a.nr_titulo = b.nr_titulo 
and a.nr_adiantamento = nr_adiantamento_p 
 

union
 
 
SELECT	1 qt_registro 
from	movto_trans_financ a 
where	a.nr_adiant_pago		= nr_adiantamento_p 
 

union
 
 
select	1 qt_registro 
from	ordem_compra a, 
	ordem_compra_adiant_pago b 
where	a.nr_ordem_compra		= b.nr_ordem_compra 
and	(a.nr_seq_motivo_cancel IS NOT NULL AND a.nr_seq_motivo_cancel::text <> '') 
and	b.nr_adiantamento		= nr_adiantamento_p 
 
) alias3;
 
return	ie_permite_alterar_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
