CREATE OR REPLACE FUNCTION obter_se_altera_regulacao ( nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE

 
ie_permite_w	varchar(1) := 'N';
count_w		bigint;
count_ww	bigint;


BEGIN 
 
select	count(*) 
into STRICT	count_w 
from	conta_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if (count_w > 0) then 
	 
	select	count(*) 
	into STRICT	count_ww 
	from	conta_paciente 
	where	ie_status_acerto = 1 
	and	nr_atendimento = nr_atendimento_p;
	 
	if (count_ww > 0) then 
		ie_permite_w := 'S';
	end if;
	 
elsif (count_w = 0) then 
	ie_permite_w := 'S';
end if;
 
return ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
