CREATE OR REPLACE FUNCTION obter_se_alt_atrib_oc ( nm_tabela_p text, nm_atributo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1);


BEGIN

select	coalesce(max(ie_permite),'N')
into STRICT	ie_permite_w
from	regra_alteracao_oc
where	nm_tabela = nm_tabela_p
