CREATE OR REPLACE FUNCTION obter_se_alt_proc_ditado ( nr_seq_prescr_proc_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

select  CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	prescr_proc_ditado
where   nr_seq_prescr_proc = nr_seq_prescr_proc_p;

if (ds_retorno_w = 'N') then

	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from    prescr_proc_ditado
	where   nm_usuario = nm_usuario_p
	and     nr_seq_prescr_proc = nr_seq_prescr_proc_p;

end if;

return	ds_retorno_w;

end;
$body$
