CREATE OR REPLACE FUNCTION obter_se_amamenta (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);


BEGIN

if (obter_sexo_pf(obter_pessoa_atendimento(nr_atendimento_p, 'C'), 'C') = 'F')then
	select max(val)
	into STRICT	ds_retorno_w
	from (SELECT 	coalesce(max('S'),'N') val
	from	nascimento_amamentacao
	where	nr_atendimento = nr_atendimento_p
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and		coalesce(dt_inativacao::text, '') = ''

union

    SELECT coalesce(max('S'),'N') val
 from historico_saude_mulher a
where (coalesce(dt_fim_amamentacao::text, '') = ''  or dt_fim_amamentacao >= clock_timestamp())
and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and coalesce(dt_inativacao::text, '') = ''
and cd_pessoa_fisica =obter_pessoa_atendimento(nr_atendimento_p, 'C')
and (dt_inicio_amamentacao IS NOT NULL AND dt_inicio_amamentacao::text <> '')
and coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP') alias18;
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
